class NLP:
    def comment_Analysis(self, commmentlist):                                           #对每条评价进行打分，最高1.0，分越低说明评价越消极
        xm.set_stopword('手机关键词停词表.txt')
        totalscore=0
        for commtent in commmentlist:
            totalscore=totalscore+xm.sentiment(commtent)
        avescore=totalscore/len(commmentlist)
        return avescore



    def keyword_extract(self,commentlist):                                             #提取关键词
        keywordRaw=[]
        keywordFinal=[]
        xm.set_stopword('手机关键词停词表.txt')
        for commtent in commentlist:
            keywordRaw=keywordRaw+xm.keyword(commtent)
        keywordReal=keywordRaw[0:40]
        for keyword in keywordReal:
            keywordFinal.append(keyword[0])


        return keywordFinal

    def comment_extract(self,commentlist):
        rank=[]
        BEST=[]
        WORST=[]
        for comment in commentlist:                 #删除长度小于30的评论
            if len(comment)<12:
                commentlist.remove(comment)

        for  comment in commentlist:
            group=(xm.sentiment(comment),comment)
            rank.append(group)

        ranklist=sorted(rank)

        num=int(len(ranklist)*0.05)

        bestComment=ranklist[num-1:num+4]
        for comment in bestComment:
            BEST.append(comment[1])

        worstComment=ranklist[-num-6:-num-1]
        for comment in  worstComment:
            WORST.append(comment[1])
        return WORST,BEST           #返回五条评分高的评价和五条比较低分的评价





    def word_cloud(self,commentlist):
        # matplotlib.use('Qt5cairo')
        import matplotlib.pyplot as plt
        #生成图云
        word=''
        for comment in commentlist:
            word=word+comment+' '
        wc = WordCloud(  # 词云
            font_path='simhei.ttf',  # 字体路劲
            background_color='white',  # 背景颜色
            width=1000,
            height=600,
            max_font_size=50,  # 字体大小
            min_font_size=10,
            mask=plt.imread('abc.jpeg'),  # 背景图片
            max_words=1000
        )
        wc.generate(word)
        wc.to_file('wc.png')  # 图片保存

